// List 自定义增删改查
import React, { useEffect, useState } from "react";
import { Avatar, Button, List, Skeleton } from "antd";

const CUSTOMLIST = [
  {
    gender: "female",
    name: {
      title: "Miss",
      first: "Munira",
      last: "Hennum",
    },
    email: "munira.hennum@example.com",
    picture: "https://randomuser.me/api/portraits/women/19.jpg",
    nat: "NO",
  },
  {
    gender: "female",
    name: {
      title: "Miss",
      first: "Nella",
      last: "Pulli",
    },
    email: "nella.pulli@example.com",
    picture: "https://randomuser.me/api/portraits/women/22.jpg",
    nat: "FI",
  },
  {
    gender: "female",
    name: {
      title: "Ms",
      first: "Ellen",
      last: "Neva",
    },
    email: "ellen.neva@example.com",
    picture: "https://randomuser.me/api/portraits/women/95.jpg",
    nat: "FI",
  },
];

const count = 3;
const fakeDataUrl = `https://randomuser.me/api/?results=${count}&inc=name,gender,email,nat,picture&noinfo`;

const TodoList = () => {
  const [initLoading, setInitLoading] = useState(true);
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState([]);
  const [list, setList] = useState([]);

  useEffect(() => {
    fetch(fakeDataUrl)
      .then((res) => res.json())
      .then((res) => {
        console.log("res", res);
        setInitLoading(false);
        setData(res.results);
        setList(res.results);
      });
  }, []);

  const onLoadMore = () => {
    setLoading(true);
    setList(
      data.concat(
        [...new Array(count)].map(() => ({
          loading: true,
          name: {},
          picture: {},
        }))
      )
    );
    fetch(fakeDataUrl)
      .then((res) => res.json())
      .then((res) => {
        const newData = data.concat(res.results);
        setData(newData);
        setList(newData);
        setLoading(false);
        // Resetting window's offsetTop so as to display react-virtualized demo underfloor.
        // In real scene, you can using public method of react-virtualized:
        // https://stackoverflow.com/questions/46700726/how-to-use-public-method-updateposition-of-react-virtualized
        window.dispatchEvent(new Event("resize"));
      });
  };

  const loadMore =
    !initLoading && !loading ? (
      <div
        style={{
          textAlign: "center",
          marginTop: 12,
          height: 32,
          lineHeight: "32px",
        }}
      >
        <Button onClick={onLoadMore}>loading more</Button>
      </div>
    ) : null;

  return (
    <div>
      <List
        loading={initLoading}
        itemLayout="horizontal"
        dataSource={CUSTOMLIST}
        renderItem={(item) => {
          // 遍历 dataSource 的数据，返回需要渲染的数据
          console.log("item", item);
          return (
            <List.Item
              actions={[
                // eslint-disable-next-line jsx-a11y/anchor-is-valid
                <a key="list-loadmore-edit">edit</a>,
                // eslint-disable-next-line jsx-a11y/anchor-is-valid
                <a key="list-loadmore-more">more</a>,
              ]}
            >
              <Skeleton avatar title={false} active>
                <List.Item.Meta
                  avatar={<Avatar src={item.picture.large} />}
                  title={<a href="#/other">{item.name?.last}</a>}
                  description={
                    <>
                      <div>邮箱📮：{item.email}</div>
                      <div>国家：{item.nat}</div>
                    </>
                  }
                />
              </Skeleton>
            </List.Item>
          );
        }}
      />
    </div>
  );
};

export default TodoList;
