// List 自定义增删改查
import React, { useState } from "react";
import { Avatar, List, Skeleton, Button, Input, Modal } from "antd";

const { Search } = Input;

const CUSTOMLIST = [
  {
    gender: "female",
    name: "Hennum",
    email: "munira.hennum@example.com",
    picture: "https://randomuser.me/api/portraits/women/19.jpg",
    nat: "NO",
  },
  {
    gender: "female",
    name: "Pulli",
    email: "nella.pulli@example.com",
    picture: "https://randomuser.me/api/portraits/women/22.jpg",
    nat: "FI",
  },
  {
    gender: "female",
    name: "Neva",
    email: "ellen.neva@example.com",
    picture: "https://randomuser.me/api/portraits/women/95.jpg",
    nat: "FI",
  },
];

const TodoList = () => {
  const [initLoading, setInitLoading] = useState(false); // loading 状态
  const [list, setList] = useState(CUSTOMLIST); // 默认 list 数据
  const [isModalOpen, setIsModalOpen] = useState(false); // 新增弹窗的显隐

  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const onSearch = (value) => {
    setInitLoading(true);
    let arr = [];
    list.forEach((item) => {
      if (item.nat === value) arr.push(item);
    });
    // 搜索结果为空时搜索结果应为全部数据
    if (value === "") {
      setList(CUSTOMLIST);
    } else {
      setList(arr);
    }
    // 延时一秒钟，模拟加载效果
    setTimeout(() => {
      setInitLoading(false);
    }, 1000);
  };

  return (
    <div>
      <Search
        placeholder="按照国家来搜索"
        allowClear
        enterButton="搜索"
        size="large"
        onSearch={onSearch}
      />
      <List
        loading={initLoading}
        itemLayout="horizontal"
        dataSource={list}
        renderItem={(item) => {
          // 遍历 dataSource 的数据，返回需要渲染的数据
          console.log("item", item);
          return (
            <List.Item
              actions={[
                // eslint-disable-next-line jsx-a11y/anchor-is-valid
                <a key="list-loadmore-edit">编辑</a>,
                // eslint-disable-next-line jsx-a11y/anchor-is-valid
                <a key="list-loadmore-more">删除</a>,
              ]}
            >
              <Skeleton avatar title={false} loading={initLoading} active>
                <List.Item.Meta
                  avatar={<Avatar src={item.picture} />}
                  title={<a href="#/other">{item.name}</a>}
                  description={
                    <>
                      <div>邮箱📮：{item.email}</div>
                      <div>国家：{item.nat}</div>
                    </>
                  }
                />
              </Skeleton>
            </List.Item>
          );
        }}
      />
      {!initLoading ? (
        <div
          style={{
            textAlign: "center",
            marginTop: 12,
            height: 32,
            lineHeight: "32px",
          }}
        >
          <Button
            onClick={() => {
              setIsModalOpen(true);
            }}
          >
            新增数据
          </Button>
        </div>
      ) : null}
      <Modal
        title="新增数据"
        open={isModalOpen}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <p>Some contents...</p>
        <p>Some contents...</p>
        <p>Some contents...</p>
      </Modal>
    </div>
  );
};

export default TodoList;
